import "./CSS/DarkLightMode.css";
import { useContext } from "react";
import { DarkLightMod } from "../../contexts/Context";

const DarkLightMode = () => {
  const { theme, setTheme } = useContext(DarkLightMod);
  console.log(theme);

  const [language , setLanguage] = useState("TR") //["TR","ENG"

   translate = (key) => {
    return language[key] || key;
  };
  
// const TR=()=>{
//   Karanlik:"Karanlık Mod",
//   Aydinlik:"Aydınlık Mod"
// }
// const ENG=()=>{
//   Dark:"Dark Mod",
//   Light:"Light Mod"
// }


  return (
    <div>
      <label htmlFor="theme-toggle" className="switch">
        <input
          id="theme-toggle"
          type="checkbox"
          onChange={() => setTheme(!theme)}
          checked={theme}
        />
        <span className="slider round"></span>
        <span className={theme === false ? "slider2 round2": ""}></span>
      </label>
      <p>{theme=== false ? "Dark Mod": "Light Mod"}</p>
    </div>
  );
};

export default DarkLightMode;
