import "./CSS/DarkLightMode.css";
import { useContext } from "react";
import { DarkLightMod } from "../../contexts/Context";

const DarkLightMode = () => {
  const { theme, setTheme } = useContext(DarkLightMod);
  console.log(theme);

  const [language , setLanguage] = useState("TR") //["TR","ENG"

const [DarkMod , setDarkMod] = useState() //["Karanlık Mod","Dark Mod"
const [LightMod , setLightMod] = useState() //["Aydınlık Mod","Light Mod"

if (language === "TR") {
  setDarkMod=("Karanlık Mod");
  setLightMod=("Aydınlık Mod");
}
else if (language === "ENG") {
  setDarkMod=("Dark Mod");
    setLightMod=("Light Mod");
}



  return (
    <div>
      <label htmlFor="theme-toggle" className="switch">
        <input
          id="theme-toggle"
          type="checkbox"
          onChange={() => setTheme(!theme)}
          checked={theme}
        />
        <span className="slider round"></span>
        <span className={theme === false ? "slider2 round2": ""}></span>
      </label>
      <p>{theme=== false ? DarkMod: LightMod}</p>
    </div>
  );
};

export default DarkLightMode;
