import { createContext, useEffect, useState } from "react";

export const LanguageVerisi = createContext();

const LanguageTuru = ({ children }) => {
  const systemLanguage = navigator.userLanguage;
  console.log(systemLanguage);

  const storedLanguage = JSON.parse(localStorage.getItem("language"));

  const [language, setLanguage] = useState(
    localStorage.getItem("language") === null ? systemLanguage : storedLanguage
  );

  useEffect(() => {
    localStorage.setItem("language", JSON.stringify(language));
  }, [language]);

  const contextState = {
    language: language,
    setLanguage: setLanguage,
  };

  return (
    <LanguageVerisi.Provider value={contextState}>
      {children}
    </LanguageVerisi.Provider>
  );
};

export default LanguageTuru;
