import { createContext, useState } from "react";

export const DarkLightMod = createContext();

const DarkLightTheme = ({ children }) => {
    const darkModeMediaQuery = window.matchMedia("(prefers-color-scheme: lighy)");

    const [theme, setTheme] = useState(darkModeMediaQuery);

    const contextState = {
        theme: theme,
        setTheme: setTheme,
    };
  
    return (
      <DarkLightMod.Provider value={contextState}>
        {children}
      </DarkLightMod.Provider>
    );
  };
  
  export default DarkLightTheme;
