import { createContext, useEffect, useState } from "react";

export const DarkLightMod = createContext();

const DarkLightTheme = ({ children }) => {
    const isDarkMode = window.matchMedia("(prefers-color-scheme: dark)").matches;
  const [theme, setTheme] = useState({isDarkMode});

  useEffect(() => {
    localStorage.setItem("cartData", JSON.stringify(theme));
  }, [cart]);

  const contextState = {
    theme: theme,
    setTheme: setTheme,
  };

console.log(isDarkMode);
  return (
    <DarkLightMod.Provider value={contextState}>
      {children}
    </DarkLightMod.Provider>
  );
};

export default DarkLightTheme;
