import { createContext, useState } from "react";

export const DarkLightMod = createContext();

const Dark = ({ children }) => {
    const [products, setProducts] = useState("");
  
    const contextState = {
      Urunler: products,
      setUrunler: setProducts,
    };
  
    return (
      <Dark.Provider value={contextState}>
        {children}
      </Dark.Provider>
    );
  };
  
  export default Dark;
