import React, { useContext, useEffect, useState } from "react";

import data from "../Data/Data.js";

import ".//CSS/Projects.css";
import { LanguageVerisi } from "../contexts/LanguageContext";
const Projects = () => {
  const { language } = useContext(LanguageVerisi);

  const [projeAdi, setProjeAdi] = useState();
  const [projeAciklamasi, setProjeAciklamasi] = useState();
  const [siteyeGit, setSiteyeGit] = useState();

  return (
    <div id="ProjectsMain">
      <div id="ProjectsContainer1"></div>
      <div id="ProjectsContainer2">
        <div className="Sayfalarim">
        {data
  .filter((item) => item.language === language)
  .map((item) => (
    <div key={item.id}>
      <h4>{item.title}</h4>
      <p>{item.description}</p>
      <img src={`/images/${item.image}`} alt={item.title} />
      <p>
        <a href={item.websiteLink}>Siteye Git</a>
      </p>
      <p>
        Yazılımlar: {item.yazilimlari.map((yazilim) => <span key={yazilim}>{yazilim}</span>)}
      </p>
    </div>
  ))}
        </div>
      </div>
    </div>
  );
};

export default Projects;
