import React, { useContext } from "react";
import Home from "./layout/Home";
import { DarkLightMod } from "./contexts/Context";
import "./app.css";
function App() {
  const { theme } = useContext(DarkLightMod);

  console.log(theme);
  useEffect(() => {
    document.body.className = theme ? "dark-mode" : "light-mode";
  }, [theme]);
  return (
    <div className={theme  ? 'dark-mode' : 'light-mode'}>
      <Home />
    </div>
  );
}

export default App;
