import { createContext, useEffect, useState } from "react";

export const DarkLightMod = createContext();

const DarkLightTheme = ({ children }) => {

    
  const [theme, setTheme] = useState();



  useEffect(() => {
    const darkModeMediaQuery = window.matchMedia("(prefers-color-scheme: dark)");

    const handleSystemThemeChange = (event) => {
      const isDarkMode = event.matches;
      if (!localStorage.getItem("theme")) {
        setTheme(isDarkMode ? "dark" : "light");
      }
    };
    setTheme(darkModeMediaQuery.matches);
    darkModeMediaQuery.addListener(handleSystemThemeChange);

    return () => {
      darkModeMediaQuery.removeListener(handleSystemThemeChange);
    };
  }, []);

  useEffect(() => {
    localStorage.setItem("theme", JSON.stringify(theme));
  }, [theme]);



  const contextState = {
    theme: theme,
    setTheme: setTheme ,
  };

  return (
    <DarkLightMod.Provider value={contextState}>
      {children}
    </DarkLightMod.Provider>
  );
};

export default DarkLightTheme;
