import React, { useContext } from "react";
import { LanguageTuru } from "../../contexts/LanguageContext";
const Language = () => {
  const [language, setLanguage] = useContext(LanguageTuru); //["TR","ENG"]
  const { theme, setTheme } = useContext(DarkLightMod);

  return (
    <div>
      <button>
        {language === "tr-TR" ? "Türkçeye Geç" : " İngilizceye Geç"}
      </button>
    </div>
  );
};

export default Language;
